import { Component } from "react";
import React from 'react' ;
import UserService from "../service/UserService";


class UserUpdateComponent extends Component
{

    constructor(props)
    {
        super(props)
        this.retriverUser = this.retriverUser.bind(this);
        this.state = {
            id : this.props.match.params.id ,
            firstName : '' ,
            lastName : '' ,
            phoneNo : '' ,
            email : '' 
        }
    }


componentDidMount()
{

    if(this.state.id == -1)
        return

    this.retriverUser(this.state.id);
}

retriverUser(id)
{
    return UserService.retrieveUser(id).then(
        response => {
            this.setState( {
                firstName :  response.data.firstName ,
                lastName : response.data.lastName ,
                phoneNo : response.data.phoneNo ,
                email : response.data.email 
            } )
        }

    )

}


    render()
    {

        let { firstName , lastName , phoneNo , email } = this.state ;

       return(
           <div className="container">


                <table>
                <thead>
                    <tr>
                    <th>First Name</th>
<th>Last Name</th>
<th>Phone No</th>
<th>Email</th>
                        </tr>                    


                </thead>

            <tbody>

            <tr>



       {/* <td>{this.state.firstName}</td>
<td>{this.state.lastName}</td>
<td>{this.state.phoneNo}</td>
<td>{this.state.email}</td> */}

              <td>firstName}</td>
<td>{this.state.lastName}</td>
<td>{this.state.phoneNo}</td>
<td>{this.state.email}</td>



                        </tr>                    



            </tbody>
                </table>



           </div>
       )     

    }


}

export default UserUpdateComponent ;
